mapper.select:
SELECT IFNULL(cast(id as char),"") id, IFNULL(mobile,"") as mobile, IFNULL(name,"") as name, IFNULL(birth,"") as birth, IFNULL(password,"") as password, IFNULL(cast(gender as char),"") gender, IFNULL(description,"") as description, IFNULL(constellation,"") as constellation, IFNULL(face_image,"") as face_image FROM uhoem_member WHERE id = id
and mobile=#{mobile}
and password=#{password}

mapper.update:
UPDATE uhoem_member SET
password=#{password}
WHERE
mobile=#{mobile}

UPDATE uhoem_member SET

	id=#{memberid}


	,mobile=#{mobile}


	,name=#{name}


	,gender=#{gender}


	,birth=#{birth}


	,description=#{description}


	,constellation=#{constellation}


	,face_image=#{face_image}

WHERE
id=#{memberid}
