mapper.update:
UPDATE xx_order u
u.order_status = #{order_status}, u.payment_status = #{payment_status} , u.shipping_status = #{shipping_status} ,
WHERE 1 = 1 AND id = #{orderId}

mapper.select:
SELECT u.id, u.create_date, u.expire, u.sn, u.order_status FROM xx_order u WHERE u.member = #{memberId}
AND u.order_status IN (3,4)
AND u.order_status = #{type}
ORDER BY u.id DESC

mapper.update:
UPDATE xx_order o
o.address = #{address}, o.area_name = #{area_name}, o.consignee = #{consignee}, o.expire = #{expire}, o.phone = #{phone}, o.zip_code = #{zip_code}, o.area = #{area},
WHERE o.id = #{orderId}
