mapper.select:
SELECT	
	uat.status
FROM
	uhoem_accept_member_task uat
WHERE
	uat.task_id = #{taskId}
AND
	uat.member_id = #{memberId}
AND
	uat.project_id = #{projectId}

SELECT uamt.id, uamt.task_id, uamt.project_id, uamt.ctime FROM uhoem_accept_member_task uamt WHERE 1 = 1
AND uamt.member_id = #{memberId}
AND uamt.task_id = #{taskId}
ORDER BY uamt.task_id

mapper.update:
UPDATE
	uhoem_accept_member_task uamt
	
		
			uamt.status = #{status},
		
		
			uamt.score = #{score}
		
	
WHERE
	uamt.task_id = #{taskId}
AND
	uamt.project_id = #{projectId}

UPDATE
	uhoem_accept_member_node
SET
	status = 1
WHERE
	node_id = #{nodeId}
AND
	member_id = #{memberId}
AND
	project_id = #{projectId}

UPDATE	
	uhoem_accept_member_task 
SET
	accept = #{accept}
WHERE
	id = #{memberTaskId}
